import{_ as e,o as p,c as s,d as t,a as i}from"./app.de9d028f.js";const l={},n=t('<h1 id="Terms_of_Use" style="font-size:45px;"><a href="#Terms_of_Use" class="header-anchor">#</a> \u8A8D\u77E5\u8207\u63A5\u53D7\u689D\u6B3E </h1><div class="step">\u6B61\u8FCE\u60A8\u4F7F\u7528Topic\u7DB2\u7AD9\u3002\u7576\u60A8\u8A3B\u518A\u5B8C\u6210\u6216\u958B\u59CB\u4F7F\u7528Topic\u7DB2\u7AD9\u6240\u63D0\u4F9B\u4E4B\u5404\u9805\u670D\u52D9\u6642\uFF0C \u5373\u8996\u70BA\u60A8\u5DF2\u7D93\u95B1\u8B80\u3001\u4E86\u89E3\u4E26\u540C\u610F\u300CTopic\u57FA\u672C\u689D\u6B3E\u300D\u3002\u5982\u679C\u60A8\u4E0D\u540C\u610F\u672C\u670D\u52D9\u689D\u6B3E\u7684\u5167\u5BB9\uFF0C \u60A8\u61C9\u7ACB\u5373\u505C\u6B62\u4F7F\u7528Topic\u7DB2\u7AD9\u3002</div><h1 id="Terms_of_Use_Main" style="font-size:45px;"><a href="#Terms_of_Use_Main" class="header-anchor">#</a> Topic\u57FA\u672C\u689D\u6B3E </h1><div class="step">\u4E00\u3001\u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u7684\u9069\u7528\u7BC4\u570D</div><p style="font-size:18px;margin-left:35px;"> \u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u5167\u5BB9\uFF0C\u5305\u62EC\u672C\u7DB2\u7AD9\u5982\u4F55\u8655\u7406\u5728\u60A8\u4F7F\u7528\u7DB2\u7AD9\u670D\u52D9\u6642\u6536\u96C6\u5230\u7684\u500B\u4EBA\u8B58\u5225\u8CC7\u6599\u3002 \u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u4E0D\u9069\u7528\u65BC\u672C\u7DB2\u7AD9\u4EE5\u5916\u7684\u76F8\u95DC\u9023\u7D50\u7DB2\u7AD9\uFF0C\u4E5F\u4E0D\u9069\u7528\u65BC\u975E\u672C\u7DB2\u7AD9\u6240\u59D4\u8A17\u6216\u53C3\u8207\u7BA1\u7406\u7684\u4EBA\u54E1\u3002 </p><div class="step">\u4E8C\u3001\u500B\u4EBA\u8CC7\u6599\u7684\u8490\u96C6\u3001\u8655\u7406\u53CA\u5229\u7528\u65B9\u5F0F</div><p style="font-size:18px;margin-left:35px;"> 1.\u7576\u60A8\u9020\u8A2A\u672C\u7DB2\u7AD9\u6216\u4F7F\u7528\u672C\u7DB2\u7AD9\u6240\u63D0\u4F9B\u4E4B\u529F\u80FD\u670D\u52D9\u6642\uFF0C\u6211\u5011\u5C07\u8996\u8A72\u670D\u52D9\u529F\u80FD\u6027\u8CEA\uFF0C\u8ACB\u60A8\u63D0\u4F9B\u5FC5\u8981\u7684\u500B\u4EBA\u8CC7\u6599\uFF0C\u4E26\u5728\u8A72\u7279\u5B9A\u76EE\u7684\u7BC4\u570D\u5167\u8655\u7406\u53CA\u5229\u7528\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\uFF1B\u975E\u7D93\u60A8\u66F8\u9762\u540C\u610F\uFF0C\u672C\u7DB2\u7AD9\u4E0D\u6703\u5C07\u500B\u4EBA\u8CC7\u6599\u7528\u65BC\u5176\u4ED6\u7528\u9014\u3002 </p><p style="font-size:18px;margin-left:35px;"> 2.\u672C\u7DB2\u7AD9\u5728\u60A8\u4F7F\u7528\u670D\u52D9\u4FE1\u7BB1\u3001\u554F\u5377\u8ABF\u67E5\u7B49\u4E92\u52D5\u6027\u529F\u80FD\u6642\uFF0C\u6703\u4FDD\u7559\u60A8\u6240\u63D0\u4F9B\u7684\u59D3\u540D\u3001\u96FB\u5B50\u90F5\u4EF6\u5730\u5740\u3001\u806F\u7D61\u65B9\u5F0F\u53CA\u4F7F\u7528\u6642\u9593\u7B49\u3002 </p><p style="font-size:18px;margin-left:35px;"> 3.\u65BC\u4E00\u822C\u700F\u89BD\u6642\uFF0C\u4F3A\u670D\u5668\u6703\u81EA\u884C\u8A18\u9304\u76F8\u95DC\u884C\u5F91\uFF0C\u5305\u62EC\u60A8\u4F7F\u7528\u9023\u7DDA\u8A2D\u5099\u7684 IP \u4F4D\u5740\u3001\u4F7F\u7528\u6642\u9593\u3001\u4F7F\u7528\u7684\u700F\u89BD\u5668\u3001\u700F\u89BD\u53CA\u9EDE\u9078\u8CC7\u6599\u8A18\u9304\u7B49\uFF0C\u505A\u70BA\u6211\u5011\u589E\u9032\u7DB2\u7AD9\u670D\u52D9\u7684\u53C3\u8003\u4F9D\u64DA\uFF0C\u6B64\u8A18\u9304\u70BA\u5167\u90E8\u61C9\u7528\uFF0C\u6C7A\u4E0D\u5C0D\u5916\u516C\u4F48\u3002 </p><p style="font-size:18px;margin-left:35px;"> 4.\u60A8\u53EF\u4EE5\u96A8\u6642\u5411\u6211\u5011\u63D0\u51FA\u8ACB\u6C42\uFF0C\u4EE5\u66F4\u6B63\u6216\u522A\u9664\u60A8\u7684\u5E33\u6236\u6216\u672C\u7DB2\u7AD9\u6240\u8490\u96C6\u7684\u500B\u4EBA\u8CC7\u6599\u7B49\u96B1\u79C1\u8CC7\u8A0A\u3002\u806F\u7E6B\u65B9\u5F0F\u8ACB\u898B\u53F3\u4E0A\u89D2\u7684\u300C\u806F\u7D61\u6211\u5011\u300D\u3002 </p><div class="step">\u4E09\u3001\u8CC7\u6599\u4E4B\u4FDD\u8B77</div><p style="font-size:18px;margin-left:35px;"> 1.\u672C\u7DB2\u7AD9\u4E3B\u6A5F\u5747\u8A2D\u6709\u9632\u706B\u7246\u3001\u9632\u6BD2\u7CFB\u7D71\u7B49\u76F8\u95DC\u7684\u5404\u9805\u8CC7\u8A0A\u5B89\u5168\u8A2D\u5099\u53CA\u5FC5\u8981\u7684\u5B89\u5168\u9632\u8B77\u63AA\u65BD\uFF0C\u52A0\u4EE5\u4FDD\u8B77\u7DB2\u7AD9\u53CA\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\u63A1\u7528\u56B4\u683C\u7684\u4FDD\u8B77\u63AA\u65BD\uFF0C\u53EA\u7531\u7D93\u904E\u6388\u6B0A\u7684\u4EBA\u54E1\u624D\u80FD\u63A5\u89F8\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\uFF0C\u76F8\u95DC\u8655\u7406\u4EBA\u54E1\u7686\u7C3D\u6709\u4FDD\u5BC6\u5408\u7D04\uFF0C\u5982\u6709\u9055\u53CD\u4FDD\u5BC6\u7FA9\u52D9\u8005\uFF0C\u5C07\u6703\u53D7\u5230\u76F8\u95DC\u7684\u6CD5\u5F8B\u8655\u5206\u3002 </p><p style="font-size:18px;margin-left:35px;"> 2.\u5982\u56E0\u696D\u52D9\u9700\u8981\u6709\u5FC5\u8981\u59D4\u8A17\u5176\u4ED6\u55AE\u4F4D\u63D0\u4F9B\u670D\u52D9\u6642\uFF0C\u672C\u7DB2\u7AD9\u4EA6\u6703\u56B4\u683C\u8981\u6C42\u5176\u9075\u5B88\u4FDD\u5BC6\u7FA9\u52D9\uFF0C\u4E26\u4E14\u63A1\u53D6\u5FC5\u8981\u6AA2\u67E5\u7A0B\u5E8F\u4EE5\u78BA\u5B9A\u5176\u5C07\u78BA\u5BE6\u9075\u5B88\u3002 </p><div class="step">\u56DB\u3001\u7DB2\u7AD9\u5C0D\u5916\u7684\u76F8\u95DC\u9023\u7D50</div><p style="font-size:18px;margin-left:35px;"> \u672C\u7DB2\u7AD9\u7684\u7DB2\u9801\u63D0\u4F9B\u5176\u4ED6\u7DB2\u7AD9\u7684\u7DB2\u8DEF\u9023\u7D50\uFF0C\u60A8\u4E5F\u53EF\u7D93\u7531\u672C\u7DB2\u7AD9\u6240\u63D0\u4F9B\u7684\u9023\u7D50\uFF0C\u9EDE\u9078\u9032\u5165\u5176\u4ED6\u7DB2\u7AD9\u3002\u4F46\u8A72\u9023\u7D50\u7DB2\u7AD9\u4E0D\u9069\u7528\u672C\u7DB2\u7AD9\u7684\u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\uFF0C\u60A8\u5FC5\u9808\u53C3\u8003\u8A72\u9023\u7D50\u7DB2\u7AD9\u4E2D\u7684\u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u3002 </p><div class="step">\u4E94\u3001\u8207\u7B2C\u4E09\u4EBA\u5171\u7528\u500B\u4EBA\u8CC7\u6599\u4E4B\u653F\u7B56</div><p style="font-size:18px;margin-left:35px;"> \u672C\u7DB2\u7AD9\u7D55\u4E0D\u6703\u63D0\u4F9B\u3001\u4EA4\u63DB\u3001\u51FA\u79DF\u6216\u51FA\u552E\u4EFB\u4F55\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\u7D66\u5176\u4ED6\u500B\u4EBA\u3001\u5718\u9AD4\u3001\u79C1\u4EBA\u4F01\u696D\u6216\u516C\u52D9\u6A5F\u95DC\uFF0C\u4F46\u6709\u6CD5\u5F8B\u4F9D\u64DA\u6216\u5408\u7D04\u7FA9\u52D9\u8005\uFF0C\u4E0D\u5728\u6B64\u9650\u3002<span style="color:#c84b31;">\u524D\u9805\u4F46\u66F8\u4E4B\u60C5\u5F62\u5305\u62EC\u4E0D\u9650\u65BC\uFF1A</span></p><p style="font-size:18px;margin-left:35px;"> 1.\u7D93\u7531\u60A8\u66F8\u9762\u540C\u610F\u3002 </p><p style="font-size:18px;margin-left:35px;"> 2.\u6CD5\u5F8B\u660E\u6587\u898F\u5B9A\u3002 </p><p style="font-size:18px;margin-left:35px;"> 3.\u70BA\u514D\u9664\u60A8\u751F\u547D\u3001\u8EAB\u9AD4\u3001\u81EA\u7531\u6216\u8CA1\u7522\u4E0A\u4E4B\u5371\u96AA\u3002 </p><p style="font-size:18px;margin-left:35px;"> 4.\u8207\u516C\u52D9\u6A5F\u95DC\u6216\u5B78\u8853\u7814\u7A76\u6A5F\u69CB\u5408\u4F5C\uFF0C\u57FA\u65BC\u516C\u5171\u5229\u76CA\u70BA\u7D71\u8A08\u6216\u5B78\u8853\u7814\u7A76\u800C\u6709\u5FC5\u8981\uFF0C\u4E14\u8CC7\u6599\u7D93\u904E\u63D0\u4F9B\u8005\u8655\u7406\u6216\u8490\u96C6\u8005\u4F9D\u5176\u63ED\u9732\u65B9\u5F0F\u7121\u5F9E\u8B58\u5225\u7279\u5B9A\u4E4B\u7576\u4E8B\u4EBA\u3002 </p><p style="font-size:18px;margin-left:35px;"> 5.\u7576\u60A8\u5728\u7DB2\u7AD9\u7684\u884C\u70BA\uFF0C\u9055\u53CD\u670D\u52D9\u689D\u6B3E\u6216\u53EF\u80FD\u640D\u5BB3\u6216\u59A8\u7919\u7DB2\u7AD9\u8207\u5176\u4ED6\u4F7F\u7528\u8005\u6B0A\u76CA\u6216\u5C0E\u81F4\u4EFB\u4F55\u4EBA\u906D\u53D7\u640D\u5BB3\u6642\uFF0C\u7D93\u7DB2\u7AD9\u7BA1\u7406\u55AE\u4F4D\u7814\u6790\u63ED\u9732\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\u662F\u70BA\u4E86\u8FA8\u8B58\u3001\u806F\u7D61\u6216\u63A1\u53D6\u6CD5\u5F8B\u884C\u52D5\u6240\u5FC5\u8981\u8005\u3002 </p><p style="font-size:18px;margin-left:35px;"> 6.\u6709\u5229\u65BC\u60A8\u7684\u6B0A\u76CA\u3002 </p><p style="font-size:18px;margin-left:35px;"> 7.\u672C\u7DB2\u7AD9\u59D4\u8A17\u5EE0\u5546\u5354\u52A9\u8490\u96C6\u3001\u8655\u7406\u6216\u5229\u7528\u60A8\u7684\u500B\u4EBA\u8CC7\u6599\u6642\uFF0C\u5C07\u5C0D\u59D4\u5916\u5EE0\u5546\u6216\u500B\u4EBA\u5584\u76E1\u76E3\u7763\u7BA1\u7406\u4E4B\u8CAC\u3002 </p><div class="step">\u516D\u3001Cookie \u4E4B\u4F7F\u7528</div><p style="font-size:18px;margin-left:35px;"> \u70BA\u4E86\u63D0\u4F9B\u60A8\u6700\u4F73\u7684\u670D\u52D9\uFF0C\u672C\u7DB2\u7AD9\u6703\u5728\u60A8\u7684\u96FB\u8166\u4E2D\u653E\u7F6E\u4E26\u53D6\u7528\u6211\u5011\u7684 Cookie\uFF0C\u82E5\u60A8\u4E0D\u9858\u63A5\u53D7 Cookie \u7684\u5BEB\u5165\uFF0C\u60A8\u53EF\u5728\u60A8\u4F7F\u7528\u7684\u700F\u89BD\u5668\u529F\u80FD\u9805\u4E2D\u8A2D\u5B9A\u96B1\u79C1\u6B0A\u7B49\u7D1A\u70BA\u9AD8\uFF0C\u5373\u53EF\u62D2\u7D55 Cookie \u7684\u5BEB\u5165\uFF0C\u4F46\u53EF\u80FD\u6703\u5C0E\u81F4\u7DB2\u7AD9\u67D0\u4E9B\u529F\u80FD\u7121\u6CD5\u6B63\u5E38\u57F7\u884C \u3002 </p><div class="step">\u4E03\u3001\u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u4E4B\u4FEE\u6B63</div><p style="font-size:18px;margin-left:35px;"> \u672C\u7DB2\u7AD9\u96B1\u79C1\u6B0A\u4FDD\u8B77\u653F\u7B56\u5C07\u56E0\u61C9\u9700\u6C42\u96A8\u6642\u9032\u884C\u4FEE\u6B63\uFF0C\u4FEE\u6B63\u5F8C\u7684\u689D\u6B3E\u5C07\u520A\u767B\u65BC\u7DB2\u7AD9\u4E0A\u3002 </p><div class="step">\u516B\u3001\u6A94\u6848\u4E0A\u50B3\u898F\u7BC4</div><p style="font-size:18px;margin-left:35px;"> \u6B64\u7DB2\u7AD9\u7684\u4E0A\u50B3\u529F\u80FD\u6BCF\u500B\u4EBA\u90FD\u80FD\u4F7F\u7528\uFF0C<span style="color:#c84b31;">\u4E00\u65E6\u60A8\u9055\u53CD\u4EE5\u4E0B\u5E7E\u9EDE\u898F\u7BC4</span>\uFF0C\u672C\u7DB2\u7AD9\u5C07\u6709\u6B0A\u5C0D\u60A8\u4E0A\u50B3\u7684\u6A94\u6848\u9032\u884C\u4E0B\u67B6\u4EE5\u53CA\u9650\u5236\u60A8\u53EF\u4F7F\u7528\u7684\u670D\u52D9\u7BC4\u570D\u3002 </p><p style="font-size:18px;margin-left:35px;"> 1.\u8ACB\u4E0D\u8981\u4E0A\u50B3\u8207\u8A66\u5377\u7121\u95DC\u7684\u6A94\u6848 </p><p style="font-size:18px;margin-left:35px;"> 2.\u8ACB\u52FF\u6FEB\u7528\u4E0A\u50B3\u7CFB\u7D71\u3002 </p><p style="font-size:18px;margin-left:35px;"> 3.\u6A94\u6848\u6A19\u984C\u8ACB\u52FF\u8207\u6A94\u6848\u5167\u5BB9\u7121\u95DC\u3002 </p><div class="step">\u4E5D\u3001\u514D\u8CAC\u8072\u660E</div><p style="font-size:18px;margin-left:35px;"> \u672C\u7DB2\u7AD9\u7684\u521D\u8877\u662F\u70BA\u4E86\u8B93\u5E2B\u751F\u53EF\u4EE5\u4E0D\u53D7\u7A7A\u9593\u9650\u5236\u7684\u4E0B\u8F09\u8A66\u5377\u4F86\u4F7F\u7528\uFF0C\u6211\u5011\u53EA\u662F\u4E00\u500B\u63D0\u4F9B\u60A8\u5206\u4EAB\u6A94\u6848\u7684\u5E73\u53F0\uFF0C <span style="color:#c84b31;">\u672C\u7DB2\u7AD9\u4E0A\u7684\u5167\u5BB9\u8207\u6211\u5011\u7121\u95DC\uFF0C\u672C\u7DB2\u7AD9\u50C5\u63D0\u4F9B\u5132\u5B58\u53CA\u5206\u4EAB\u670D\u52D9\u3002</span> \u82E5\u60A8\u8A8D\u70BA\u672C\u7DB2\u7AD9\u4E0A\u7684\u5167\u5BB9\u4FB5\u72AF\u5230\u4E86\u60A8\u7684\u6B0A\u76CA\uFF0C\u8ACB\u900F\u904E\u53F3\u4E0A\u89D2\u7684\u300C\u806F\u7D61\u6211\u5011\u300D\u4F86\u9032\u884C\u9023\u7D61\uFF0C\u6211\u5011\u5C07\u4EE5\u6700\u5FEB\u7684\u901F\u5EA6\u56DE\u8986\u3002 </p><div class="step">\u5341\u3001\u806F\u7E6B\u7BA1\u9053</div><p style="font-size:18px;margin-left:35px;"> \u5C0D\u65BC\u672C\u7AD9\u4E4B\u96B1\u79C1\u6B0A\u653F\u7B56\u6709\u4EFB\u4F55\u7591\u554F\uFF0C\u6216\u8005\u60F3\u63D0\u51FA\u8B8A\u66F4\u3001\u79FB\u9664\u500B\u4EBA\u8CC7\u6599\u4E4B\u8ACB\u6C42\uFF0C\u806F\u7E6B\u65B9\u5F0F\u8ACB\u898B\u53F3\u4E0A\u89D2\u7684\u300C\u806F\u7D61\u6211\u5011\u300D\u3002\u3002 </p>',37),o=i("div",{class:"drag-area-upload-buttonO",onclick:"window.open('https://topic.com.tw/','_blank');"},"\u25B6\u524D\u5F80\u7DB2\u7AD9\u25C0",-1),a=[n,o];function f(x,r){return p(),s("div",null,a)}var d=e(l,[["render",f],["__file","index.html.vue"]]);export{d as default};
